import React, {Component} from 'react';
import {CartContext} from './cart';
import {Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button } from 'reactstrap';

const {Products} = this.props
export function renderProduct(Products){
    return(
    <Row>{
        Products.map(product =>
        <Col className="Customcard" key={product.id} sm='3'>
        <Card >
        <CardImg top width="100%" src={product.imageUrl} alt="Card image cap" />
        <CardBody>
        < CardTitle>{product.name}</CardTitle>
        <CardSubtitle>Card subtitle</CardSubtitle>
        <CardText className="customText">{product.decrisption}</CardText>
        <CartContext.Consumer>
          {
            ({addToCart})=><Button onClick={() => addToCart(product)}>Add to cart</Button>
          }
          </CartContext.Consumer>
        </CardBody>
        </Card>
        </Col>
        )}
    </Row>
    )}
