import React,{Component} from 'react';

class Home extends Component{
    constructor(props){
        super(props);
        this.state = {second :0};
        this.count = this.count.bind(this)
    }

    count(){
        this.setState((prev) =>({
            second : prev.second + 1
        })
    )}

    componentDidMount(){
        setInterval(count(),1000)
    }

    render(){
        return (
            <div>
                <h1>đếm nào</h1>
                <p>Second : {this.state.second}</p>
            </div>
        )
    }
}

export default Home
