import React, {Component} from 'react';
import {CartContext} from './dataCart';

export default class Details extends Component{
    constructor(props){
        super(props);
    }
    render(){
        return(
            <div className='container'>
                <h2 className='detail-title'>iPhone 7 Plus 32GB</h2>
                <div className='details-content'>
                <img style={{height: '300px', marginTop: '20px'}} src='https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-gold-400x460.png'/>
                <div className='detailsCol2'>
                    <h5>Price: <span>$300</span></h5>
                    <CartContext.Consumer>
                        {
                            ({addToCart})=> <button onClick={()=>addToCart()}>Add to cart</button>
                        }
                    </CartContext.Consumer>
                    <h3>Smart phone</h3>
                    <p>Smartphones combine cellular radio technology with specially designed processors.
                         As smartphones have evolved, app developers have found new, ingenious ways to use hardware and wireless connectivity to make information instantly available to users. 
                         Larger, more accurate touch displays enable multi-window computing with simultaneous input from all 10 fingers.
                         Efficient multitasking and abundant memory enable recently used apps to reside in a stack without slowing down performance</p>
                </div>
                <div className='parameter'>
                    <p>Specifications</p>
                <ul>
                    <li>thích thì mua chứ nhức cái gì nữa </li>
                </ul>
                </div>
                </div>
            </div>
        )
    }
}
