import React, {Component} from 'react';
import {CartContext} from './cart';
import {Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button } from 'reactstrap';


export default function renderProducts(props){
        const {products} = props;
        return(
            <div className ='container'>
            <Row>{
                products.map(item =>
                <Col className="Customcard" key={item.id} sm='3'>
                <Card >
                <CardImg top width="100%" src={item.imageUrl} alt="Card image cap" />
                <CardBody>
                < CardTitle>{item.name}</CardTitle>
                <CardSubtitle>Card subtitle</CardSubtitle>
                <CardText className="customText">{item.decrisption}</CardText>
                <CartContext.Consumer>
                  {
                    ({addToCart})=><Button onClick={() => addToCart(item)}>Add to cart</Button>
                  }
                  </CartContext.Consumer>
                </CardBody>
                </Card>
                </Col>
                )}
            </Row>
            </div>
        )
}
