import React, {Component} from 'react';
import {CartContext} from './dataCart';
import axios from 'axios'

export default class Details extends Component{
    constructor(props){
        super(props);
         this.state = {}  
    }

    componentDidMount(){
        axios.get('https://api.myjson.com/bins/9i782').then(res =>{
            this.setState(
                res.data
            )
        })
    }

    render(){
        const product = this.state;
        const item = {
            id: 1,
            name : product.name,
            imageUrl : product.imageUrl,
            manufacturer: product.manufacturer,
            price : product.price
        }
        return(
            <div className='container'>
                <h2 className='detail-title'>{this.state.name}</h2>
                <div className='details-content'>
                <img style={{height: '300px', marginTop: '20px'}} src={this.state.imageUrl}/>
                <div className='detailsCol2'>
                    <h5>Price: <span>{this.state.price}</span></h5>
                    <CartContext.Consumer>
                        {
                            ({addToCart})=> <button onClick={()=>addToCart(item)}>Add to cart</button>
                        }
                    </CartContext.Consumer>
                    <h3>Smart phone</h3>
                    <p>{this.state.description.join('. ')}</p>
                </div>
                <div className='parameter'>
                    <h4>Specifications</h4>
                <ul>
                    <li><span>Display: </span> <div> {this.state.Specifications.Display} </div></li>
                    <li><span>Operating System:</span> <div> {this.state.Specifications.OperatingSystem} </div></li>
                    <li><span>Processor (CPU):</span><div> {this.state.Specifications.CPU} </div></li>
                    <li><span>Memory: </span><div> {this.state.Specifications.Memory} </div></li>
                    <li><span>Storage:</span><div> {this.state.Specifications.Storage} </div></li>
                    <li><span>SIM capability:</span> <div> {this.state.Specifications.SIM} </div></li>
                    <li><span>Battery:</span> <div> {this.state.Specifications.Battery} </div></li>
                </ul>
                </div>
                </div>
            </div>
        )
    }
}
