import React,{Component} from 'react';
import {CartContext} from './dataCart';
import CartProviders from './dataCart'

import { Table } from 'reactstrap';

export default class Cart extends CartProviders{
    constructor(props){
        super(props);
        this.increase = this.increase.bind(this);
        this.reduce = this.reduce.bind(this)
    }
    increase(){
        this.setState({
            quantity: this.state.quantity + 1
        })
    }
    reduce(){
        if(this.state.quantity<=1){
            return;
        }
        this.setState({
            quantity : this.state.quantity - 1
        })
    }
   
    render(){
        return(
            <div className='container'>
        <Table striped style={{textAlign: 'center', marginTop: '40px'}}>
        <thead>
          <tr className='tableHeader'>
            <th>#</th>
            <th>Image</th>
            <th>Product name</th>
            <th>Quantity</th>
            <th>Unit Price </th>
            <th>subTotal</th>
          </tr>
        </thead>
        <tbody>
        <CartContext.Consumer>
        {
            ({cartItems}) => cartItems.map(item =>
            <tr key = {item.id}>
                <th scope="row">{cartItems.indexOf(item)+1}</th>
                <td><img src={item.imageUrl} className='cartImg'/></td>
                <td className='cartName' >{item.name}</td>
                <td className='quantity' >
                    <button onClick = {this.increase}>+</button>
                    <span>{this.state.quantity}</span>
                    <button onClick = {this.reduce}>-</button>
                </td>
                <td >{item.price}</td>
                <td >${item.price*this.state.quantity}</td>
            </tr>
            )
        }  
        </CartContext.Consumer>
        </tbody>
        </Table>
        </div>
        )
    }
}
