import React,{Component} from 'react';
import {CartContext} from './dataCart';

import { Table } from 'reactstrap';

export default class Cart extends Component{
    constructor(props){
        super(props);
    }
    render(){
        return(
            <div className='container'>
        <Table striped>
        <thead>
          <tr>
            <th>SN</th>
            <th>Image</th>
            <th>Product name</th>
            <th>Quantity</th>
            <th>Unit Price </th>
            <th>subTotal</th>
          </tr>
        </thead>
        <tbody>
        <CartContext.Consumer>
        {
            ({cartItems}) => cartItems.map(item =>
            <tr>
                <th scope="row">{item.key}</th>
                <th><img src={item.imageUrl} className='cartImg'/></th>
                <td>{item.name}</td>
                <td>1</td>
                <td>{item.price}</td>
                <td>100</td>
            </tr>
            )
        }  
        </CartContext.Consumer>
        </tbody>
        </Table>
        </div>
        )
    }
}
