import React, { Component } from 'react';
import { CartContext } from './dataCart';
import axios from 'axios'

export default class Details extends Component {
    constructor(props) {
        super(props);
        this.state = {
            product: {
                // "name":"iPhone 7 Plus 32GB",
                // "imageUrl":"https://cdn.tgdd.vn/Products/Images/42/78124/iphone-7-plus-gold-400x460.png",
                // "price":521,
                // "manufacturer":"iphone",
                // "description":[ 
                //    "Smartphones combine cellular radio technology with specially designed processor",
                //    "As smartphones have evolved, app developers have found new, ingenious ways to use hardware and wireless connectivity to make information instantly available to users",
                //    "Larger, more accurate touch displays enable multi-window computing with simultaneous input from all 10 fingers",
                //    "Efficient multitasking and abundant memory enable recently used apps to reside in a stack without slowing down performance"
                // ],
                // "Specifications":{ 
                //    "Display":"LED-backlit IPS LCD, 5.5 inch, Retina HD",
                //    "OperatingSystem":"iOS 12",
                //    "CPU":"Apple A10 Fusion 4 nhân 64-bit",
                //    "Memory":"3 GB",
                //    "Storage":"32 GB",
                //    "SIM":"1 Nano SIM, Hỗ trợ 4G",
                //    "Battery":"2900 mAh"
                // }
            }
        }
    }

    componentDidMount() {
        const urlParams = new URLSearchParams(window.location.search);
        const key = urlParams.get('key');
        let productData = () => {
            axios.get('https://api.myjson.com/bins/' + key).then(res => {
                return res.data;
            }).then(data => {
                console.log(data);
                this.setState({
                    product: data
                })
            })
        }
    }

    render() {
        const { product } = this.state;
        // const item = {
        //     id: '9i782',
        //     name : product.name,
        //     imageUrl : product.imageUrl,
        //     manufacturer: product.manufacturer,
        //     price : 521
        // }
        return (
            <div className='container'>
                <h2 className='detail-title'>{product.name}</h2>
                <div className='details-content'>
                    <img style={{ height: '300px', marginTop: '20px' }} src={product.imageUrl} />
                    <div className='detailsCol2'>
                        <h5>Price: <span>{product.price}</span></h5>
                        <CartContext.Consumer>
                            {
                                ({ addToCart }) => <button className='detail-add' onClick={() => addToCart()}>Add to cart</button>
                            }
                        </CartContext.Consumer>
                        <h3>Smart phone</h3>
                        <p>{product.description}</p>
                    </div>
                    <div className='parameter'>
                        <h4>Specifications</h4>
                        <ul>
                            <li><span>Display: </span> <div> {product.Specifications['Display']} </div></li>
                            <li><span>Operating System:</span> <div> {product.Specifications['OperatingSystem']} </div></li>
                            <li><span>Processor (CPU):</span><div> {product.Specifications['CPU']} </div></li>
                            {/* <li><span>Memory: </span><div> {product.Specifications.Memory} </div></li>
                    <li><span>Storage:</span><div> {product.Specifications.Storage} </div></li>
                    <li><span>SIM capability:</span> <div> {product.Specifications.SIM} </div></li>
                    <li><span>Battery:</span> <div> {product.Specifications.Battery} </div></li> */}
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}
